import React, { useState } from 'react';
import '../styles/Login.css';
import { useNavigate } from 'react-router-dom';

const Login: React.FC = () => {
  const navigate = useNavigate();
  
  // State to hold the input values
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    
    // Perform login logic here (e.g., API call)
    // try {
    //   const response = await fetch('/api/login', {
    //     method: 'POST',
    //     headers: {
    //       'Content-Type': 'application/json',
    //     },
    //     body: JSON.stringify({ email, password }),
    //   });

    //   if (response.ok) {
    //     // If login is successful, redirect to role selection
    //     navigate('/player-dashboard');
    //   } else {
    //     // Handle login error (e.g., show a message)
    //     const errorData = await response.json();
    //     console.error('Login failed:', errorData);
    //   }
    // } catch (error) {
    //   console.error('Error:', error);
    // }
    navigate('/player-dashboard');
  };

  return (
    <div className="login">
      <div className="login-container">
        <h2 className="login-header">Welcome Back to Cricket Tournament Management</h2>
        <p className="login-subtitle">Access your account to manage teams, track player stats, and more!</p>

        <form onSubmit={handleSubmit}>
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // Update email state
            required
          />

          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Update password state
            required
          />

          <button type="submit">Login</button>
          <a href="/forgot-password" className="forgot-password">Forgot Password?</a>
        </form>

        <p className="login-footer">Donâ€™t have an account? <a href="/signup">Sign up here</a></p>
      </div>
    </div>
  );
};

export default Login;
