// src/components/TournamentArea.tsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../../styles/TournamentArea.css';

interface Tournament {
  id: string;
  name: string;
  date: string;
  location: string;
  description: string;
}

const TournamentArea: React.FC = () => {
  const [tournaments, setTournaments] = useState<Tournament[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await axios.get('http://localhost:7000/api/tournaments'); // Update this URL based on your microservice endpoint
        setTournaments(response.data);
      } catch (err) {
        setError('Failed to fetch tournaments');
      } finally {
        setLoading(false);
      }
    };

    fetchTournaments();
  }, []);

  if (loading) return <div>Loading tournaments...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="tournament-area-container">
      <h2 className="tournament-heading">Upcoming Tournaments</h2>
      <div className="tournament-cards-container">
        {tournaments.map((tournament) => (
          <div key={tournament.id} className="tournament-card">
            <h3 className="tournament-name">{tournament.name}</h3>
            <p className="tournament-date">Date: {tournament.date}</p>
            <p className="tournament-location">Location: {tournament.location}</p>
            <p className="tournament-description">{tournament.description}</p>
            <button className="tournament-details-button">View Details</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TournamentArea;
