import React, { useState } from 'react';
import '../styles/SignUp.css';

const SignUp: React.FC = () => {
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [passwordMatch, setPasswordMatch] = useState(true);

  const handlePasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setConfirmPassword(e.target.value);
    setPasswordMatch(e.target.value === password);
  };

  return (
    <div className="signup">
      <div className="signup-container">
        <h2 className="signup-header">Join the Ultimate Cricket Tournament Platform</h2>
        <p className="signup-subtitle">Manage teams, track player stats, organize tournaments, and more!</p>

        <form>
          <label htmlFor="name">Name</label>
          <input type="text" id="name" placeholder="Enter your name" required />

          <label htmlFor="username">Username</label>
          <input type="text" id="username" placeholder="Enter your username" required />

          <label htmlFor="email">Email</label>
          <input type="email" id="email" placeholder="Enter your email" required />

          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            placeholder="Enter your password"
            required
            value={password}
            onChange={handlePasswordChange}
          />

          <label htmlFor="confirm-password">Confirm Password</label>
          <input
            type="password"
            id="confirm-password"
            placeholder="Confirm your password"
            required
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
            className={!passwordMatch ? 'input-error' : ''}
          />
          {!passwordMatch && <p className="error-text">Passwords do not match.</p>}

          <label htmlFor="role">Role</label>
          <select id="role" required>
            <option value="">Select your role</option>
            <option value="player">Player</option>
            <option value="teamManager">Team Manager</option>
            <option value="organizer">Organizer</option>
          </select>

          <button type="submit">Sign Up</button>
          <a href="/forgot-password" className="forgot-password">Forgot Password?</a>
        </form>

        <p className="signup-footer">Already have an account? <a href="/select-role">Log in</a></p>
      </div>
    </div>
  );
};

export default SignUp;
